import { GoogleSpreadsheet } from 'google-spreadsheet';
const cred = require('../util/client_secret');

const SPREADSHEET_ID = "1feSylfSB8lnu-Atk3AaZ2Z3iInWH0Dc2DINWKRMW-zM";
const SHEET_ID = 0;
const client_email = cred.client_email;
const PRIVATE_KEY = cred.private_key;

const doc = new GoogleSpreadsheet(SPREADSHEET_ID);

const appendData = async (row) => {
    console.log("data")
    try{
        await doc.useServiceAccountAuth({
            client_email:client_email,
            private_key:PRIVATE_KEY,
        });
        await doc.loadInfo();

        const sheet = doc.sheetsById[SHEET_ID];
        const result = await sheet.addRow(row);
    }catch(e){
        console.log(e);
    }

};

const newrow = {ID:1,Name:'shahrukh'}

export default appendData;
