import React from 'react';
import { Container, Col, Image, Row, Button } from 'react-bootstrap';
import { GoogleSpreadsheet } from 'google-spreadsheet';

import '../testimonies/testimonies.styles.css';
import ModalForm from '../modal/modal.component';
import user from '../testimonies/user.jpg';

class Testimonies extends React.Component{
    constructor(){
        super()
        this.state = {
          data:[],
          show:false
        }
    }
    async readData(){
      const SPREADSHEET_ID = process.env.React_App_SPREADSHEET_ID;
      const client_email = process.env.React_App_CLIENT_EMAIL;
      const PRIVATE_KEY = process.env.React_App_PRIVATE_KEY;
      const doc = new GoogleSpreadsheet(SPREADSHEET_ID);
      try{
        await doc.useServiceAccountAuth({
            client_email:client_email,
            private_key:PRIVATE_KEY,
        });
        await doc.loadInfo();
        const sheet = doc.sheetsByIndex[2];
        var arr = [];
        let count = 0;
        await (await sheet.getRows()).map(row => {
            arr [count] = Object.values(row);
            count++;
        })
        this.setState({data:arr});
      }catch(e){
        console.log(e);
      }
    } 

    btnClicked = () => {
      this.setState({show:true})
    }

    handleClose = () => {
      this.setState({show:false})
    }

    componentDidMount(){
      this.readData();
    }

    render(){
        return(
            <>
              <Container>
                <span>Testimonies</span>
                <span className = "btn">
                  <Button className = "btnAdd" onClick = {this.btnClicked}>Add Testimonies</Button>
                </span>
                {
                  this.state.data.map(row =>
                    <Row key = {row[1]} className = "rows">
                      <Col>
                        <Image className = "img" src={user}/>
                        <Col>
                          <span className = "name" >{row[3]}</span>
                        </Col>
                      </Col>
                      <Col className = "col">
                        <p>{row[4]}</p>
                      </Col>
                    </Row>
                  )
                }
              </Container>  
            </>
        )
    }
}
export default Testimonies;
